DROP TABLE system_log;

CREATE TABLE IF NOT EXISTS `system_log` (
  `id` int(5) NOT NULL auto_increment,
  `username` varchar(20) default '',
  `date` date default NULL,
  `time` time default '00:00:00',
  `value` varchar(255) default NULL,
  `ip` varchar(50) default '',
  `type` enum('login','logout','email','update','file') NOT NULL,
  PRIMARY KEY  (`id`)
);

DROP TABLE field_list_value;

CREATE TABLE IF NOT EXISTS `field_list_value` (
  `id` int(10) NOT NULL auto_increment,
  `fvid` int(5) NOT NULL,
  `fvFid` int(5) default '0',
  `fvCaption` varchar(255) default '',
  `fvOrder` int(5) default '1',
